import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as c,c as d,a,b as n,d as e,e as i}from"./app-5f6064b2.js";const r="/assets/image-20230505191459817-77c01cb0.png",o="/assets/image-20230505191639333-14b1481f.png",p="/assets/image-20230505191808256-9b707afe.png",u="/assets/image-20230505192057943-68f8be35.png",m="/assets/image-20230505192228298-a5a05422.png",v="/assets/image-20230505192403403-1565ee88.png",g="/assets/image-20230505192651164-15a58eba.png",h="/assets/image-20230505194118380-69b86e01.png",b="/assets/image-20230505194147210-a4e24da1.png",y="/assets/image-20230505194631732-25b0482c.png",k="/assets/image-20230505194757700-ac66a0b5.png",_="/assets/image-20230505194327729-e56aac6d.png",f="/assets/image-20230505195308164-90054734.png",q={},x=i('<h1 id="三、mysql安装教程" tabindex="-1"><a class="header-anchor" href="#三、mysql安装教程" aria-hidden="true">#</a> 三、MySQL安装教程</h1><h2 id="_1-mysql8安装-windows下安装" tabindex="-1"><a class="header-anchor" href="#_1-mysql8安装-windows下安装" aria-hidden="true">#</a> 1 MySQL8安装(Windows下安装)</h2><h3 id="_1-1-下载zip文件" tabindex="-1"><a class="header-anchor" href="#_1-1-下载zip文件" aria-hidden="true">#</a> 1.1 下载zip文件</h3>',3),S={href:"https://dev.mysql.com/downloads/mysql/",target:"_blank",rel:"noopener noreferrer"},L=i('<figure><img src="'+r+'" alt="image-20230505191459817" tabindex="0" loading="lazy"><figcaption>image-20230505191459817</figcaption></figure><p>下载后解压即可</p><h3 id="_1-2-配置环境变量" tabindex="-1"><a class="header-anchor" href="#_1-2-配置环境变量" aria-hidden="true">#</a> 1.2 配置环境变量</h3><p>比如解压到了<code>C:\\DISH\\APP\\JAVA\\MySQL\\mysql-8.0.33-winx64</code></p><figure><img src="'+o+'" alt="image-20230505191639333" tabindex="0" loading="lazy"><figcaption>image-20230505191639333</figcaption></figure><p>配置环境变量</p><figure><img src="'+p+`" alt="image-20230505191808256" tabindex="0" loading="lazy"><figcaption>image-20230505191808256</figcaption></figure><h3 id="_1-3-创建my-ini文件" tabindex="-1"><a class="header-anchor" href="#_1-3-创建my-ini文件" aria-hidden="true">#</a> 1.3 创建<code>my.ini</code>文件</h3><div class="language-ini line-numbers-mode" data-ext="ini"><pre class="language-ini"><code><span class="token section"><span class="token punctuation">[</span><span class="token section-name selector">mysqld</span><span class="token punctuation">]</span></span>
<span class="token comment"># 设置3306端口</span>
<span class="token key attr-name">port</span><span class="token punctuation">=</span><span class="token value attr-value">3306</span>
<span class="token comment"># 设置mysql的安装目录</span>
<span class="token key attr-name">basedir</span><span class="token punctuation">=</span><span class="token value attr-value">C:/DISH/APP/JAVA/MySQL/mysql-8.0.33-winx64</span>
<span class="token comment"># 设置mysql数据库的数据的存放目录</span>
<span class="token key attr-name">datadir</span><span class="token punctuation">=</span><span class="token value attr-value">C:/DISH/APP/JAVA/MySQL/mysql-8.0.33-winx64/data</span>
<span class="token comment"># 允许最大连接数</span>
<span class="token key attr-name">max_connections</span><span class="token punctuation">=</span><span class="token value attr-value">200</span>
<span class="token comment"># 允许连接失败的次数。这是为了防止有人从该主机试图攻击数据库系统</span>
<span class="token key attr-name">max_connect_errors</span><span class="token punctuation">=</span><span class="token value attr-value">10</span>
<span class="token comment"># 服务端使用的字符集默认为UTF8</span>
<span class="token key attr-name">character-set-server</span><span class="token punctuation">=</span><span class="token value attr-value">utf8</span>
<span class="token comment"># 创建新表时将使用的默认存储引擎</span>
<span class="token key attr-name">default-storage-engine</span><span class="token punctuation">=</span><span class="token value attr-value">INNODB</span>
<span class="token comment"># 默认使用“mysql_native_password”插件认证</span>
<span class="token key attr-name">default_authentication_plugin</span><span class="token punctuation">=</span><span class="token value attr-value">mysql_native_password</span>
<span class="token section"><span class="token punctuation">[</span><span class="token section-name selector">mysql</span><span class="token punctuation">]</span></span>
<span class="token comment"># 设置mysql客户端默认字符集</span>
<span class="token key attr-name">default-character-set</span><span class="token punctuation">=</span><span class="token value attr-value">utf8</span>
<span class="token section"><span class="token punctuation">[</span><span class="token section-name selector">client</span><span class="token punctuation">]</span></span>
<span class="token comment"># 设置mysql客户端连接服务端时默认使用的端口</span>
<span class="token key attr-name">port</span><span class="token punctuation">=</span><span class="token value attr-value">3306</span>
<span class="token key attr-name">default-character-set</span><span class="token punctuation">=</span><span class="token value attr-value">utf8</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h3 id="_1-4-安装mysql" tabindex="-1"><a class="header-anchor" href="#_1-4-安装mysql" aria-hidden="true">#</a> 1.4 安装mysql</h3><h4 id="_1-4-1-以管理员身份打开cmd" tabindex="-1"><a class="header-anchor" href="#_1-4-1-以管理员身份打开cmd" aria-hidden="true">#</a> 1.4.1 以管理员身份打开cmd</h4><figure><img src="`+u+`" alt="image-20230505192057943" tabindex="0" loading="lazy"><figcaption>image-20230505192057943</figcaption></figure><h4 id="_1-4-2-在控制命令台中-进入到刚刚解压的mysql的bin目录中" tabindex="-1"><a class="header-anchor" href="#_1-4-2-在控制命令台中-进入到刚刚解压的mysql的bin目录中" aria-hidden="true">#</a> 1.4.2 在控制命令台中，进入到刚刚解压的MySQL的bin目录中</h4><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token builtin class-name">cd</span> C:<span class="token punctuation">\\</span>DISH<span class="token punctuation">\\</span>APP<span class="token punctuation">\\</span>JAVA<span class="token punctuation">\\</span>MySQL<span class="token punctuation">\\</span>mysql-8.0.33-winx64<span class="token punctuation">\\</span>bin
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><figure><img src="`+m+`" alt="image-20230505192228298" tabindex="0" loading="lazy"><figcaption>image-20230505192228298</figcaption></figure><h4 id="_1-4-3-初始化mysql数据库" tabindex="-1"><a class="header-anchor" href="#_1-4-3-初始化mysql数据库" aria-hidden="true">#</a> 1.4.3 初始化MySQL数据库</h4><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code>mysqld --initialize-insecure
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>作用：初始化数据库，并设置默认root密码为空<br> 初始化完成后，在mysql根目录中会自动生成data文件夹，如下图所示：</p><figure><img src="`+v+'" alt="image-20230505192403403" tabindex="0" loading="lazy"><figcaption>image-20230505192403403</figcaption></figure><figure><img src="'+g+`" alt="image-20230505192651164" tabindex="0" loading="lazy"><figcaption>image-20230505192651164</figcaption></figure><p>此时出现<code>mysql,performance_schema,sys</code>三个目录即表示成功</p><h4 id="_1-4-4-为windows系统安装mysql服务" tabindex="-1"><a class="header-anchor" href="#_1-4-4-为windows系统安装mysql服务" aria-hidden="true">#</a> 1.4.4 为Windows系统安装MySQL服务</h4><p>输入执行命令：mysqld install [服务名]</p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code>mysqld <span class="token function">install</span> mysql
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>作用：为Windows系统安装MySQL服务，默认服务名为：mysql<br> 注意：后面的服务名可以不写，默认的名字为 mysql。当然，如果你的电脑上需要安装多个MySQL服务，就可以用不同的名字区分了，比如 mysql5 和 mysql8。</p><h4 id="_1-4-5-启动mysql服务" tabindex="-1"><a class="header-anchor" href="#_1-4-5-启动mysql服务" aria-hidden="true">#</a> 1.4.5 启动mysql服务</h4><ul><li>启动MySQL</li></ul><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code>net start mysql
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><ul><li>停止MySQL</li></ul><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code>net stop mysql
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><ul><li>卸载MySQL服务</li></ul><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code>mysqld <span class="token parameter variable">-remove</span> mysql
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><h4 id="_1-4-6-更改密码" tabindex="-1"><a class="header-anchor" href="#_1-4-6-更改密码" aria-hidden="true">#</a> 1.4.6 更改密码</h4><ul><li>在MySQL安装目录的 bin 目录下执行命令：</li></ul><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code> mysql <span class="token parameter variable">-u</span> root <span class="token parameter variable">-p</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><blockquote><p>这时候会提示输入密码，直接回车键（上面设置的是空密码）登录成功，进入MySQL命令模式。 修改密码（ 修改密码是必须得 不然设置不了远程连接）</p></blockquote><ul><li>切换到mysql 并执行命令：</li></ul><div class="language-mysql line-numbers-mode" data-ext="mysql"><pre class="language-mysql"><code>use mysql;
ALTER USER &#39;root&#39;@&#39;localhost&#39; IDENTIFIED BY &#39;123456&#39;;
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div></div></div><p>执行上述命令之后，即可设置成功，此时mysql的用户名为：root,密码为:123456<br> 最后exit 退出，用root 在登录一次，远程连接的用户名为：root,密码为：123456</p><h2 id="_2-如何使mysql5与mysql8共存" tabindex="-1"><a class="header-anchor" href="#_2-如何使mysql5与mysql8共存" aria-hidden="true">#</a> 2 如何使MySQL5与MySQL8共存</h2><h3 id="_2-1-配置my-ini" tabindex="-1"><a class="header-anchor" href="#_2-1-配置my-ini" aria-hidden="true">#</a> 2.1 配置my.ini</h3><div class="language-ini line-numbers-mode" data-ext="ini"><pre class="language-ini"><code><span class="token section"><span class="token punctuation">[</span><span class="token section-name selector">mysqld</span><span class="token punctuation">]</span></span>

<span class="token key attr-name">port</span> <span class="token punctuation">=</span> <span class="token value attr-value">3307</span>

<span class="token key attr-name">basedir</span><span class="token punctuation">=</span><span class="token value attr-value">C:/DISH/APP/JAVA/MySQL/mysql-5.7.28-winx64/</span>
<span class="token key attr-name">datadir</span><span class="token punctuation">=</span><span class="token value attr-value">C:/DISH/APP/JAVA/MySQL/mysql-5.7.28-winx64/data</span>

<span class="token key attr-name">max_connections</span><span class="token punctuation">=</span><span class="token value attr-value">200</span>

<span class="token key attr-name">character-set-server</span><span class="token punctuation">=</span><span class="token value attr-value">utf8</span>

<span class="token key attr-name">default-storage-engine</span><span class="token punctuation">=</span><span class="token value attr-value">INNODB</span>

<span class="token key attr-name">sql_mode</span><span class="token punctuation">=</span><span class="token value attr-value">NO_ENGINE_SUBSTITUTION,STRICT_TRANS_TABLES</span>

<span class="token section"><span class="token punctuation">[</span><span class="token section-name selector">mysql</span><span class="token punctuation">]</span></span>

<span class="token key attr-name">default-character-set</span><span class="token punctuation">=</span><span class="token value attr-value">utf8</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h3 id="_2-2-以管理员身份打开cmd" tabindex="-1"><a class="header-anchor" href="#_2-2-以管理员身份打开cmd" aria-hidden="true">#</a> 2.2 以管理员身份打开cmd</h3><p>进入MySQL5的安装目录</p><p><code>C:\\DISH\\APP\\JAVA\\MySQL\\mysql-5.7.28-winx64\\bin</code></p><h3 id="_2-3-安装mysql服务" tabindex="-1"><a class="header-anchor" href="#_2-3-安装mysql服务" aria-hidden="true">#</a> 2.3 安装mysql服务</h3><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code>mysqld.exe <span class="token function">install</span> mysql5728
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><h3 id="_2-4-此时启动mysql服务会报如下错误" tabindex="-1"><a class="header-anchor" href="#_2-4-此时启动mysql服务会报如下错误" aria-hidden="true">#</a> 2.4 此时启动mysql服务会报如下错误</h3><p>More help is available by typing NET HELPMSG 3534.</p><p>先说说我遇到的情况，我通过命令启动MySQL时候出现了这个报错，然后进入服务查看的时候发现同时存在一个mysql和一个mysql5728，并且mysql是处于启动状态的</p><figure><img src="`+h+'" alt="image-20230505194118380" tabindex="0" loading="lazy"><figcaption>image-20230505194118380</figcaption></figure><p>当我右键启动MySQL的时候，也是启动不了的</p><figure><img src="'+b+'" alt="image-20230505194147210" tabindex="0" loading="lazy"><figcaption>image-20230505194147210</figcaption></figure>',53),M={href:"https://blog.csdn.net/qq_39293334/article/details/124762059",target:"_blank",rel:"noopener noreferrer"},A=i('<figure><img src="'+y+'" alt="image-20230505194631732" tabindex="0" loading="lazy"><figcaption>image-20230505194631732</figcaption></figure><blockquote><p>ps: 说明一下，在我的机器上，mysql(8)的配置是这样的</p><figure><img src="'+k+`" alt="image-20230505194757700" tabindex="0" loading="lazy"><figcaption>image-20230505194757700</figcaption></figure><p>是没有配置<code>--defaults-file</code>的，这是应该与配置有环境变量有关，又因为MySQL的环境只能配置一份，所以mysql5728(5.7.28)就没有配置环境变量，所以需要我们手动的指定<code>--defaults-file</code>的值，</p></blockquote><h3 id="_2-5-修改注册表" tabindex="-1"><a class="header-anchor" href="#_2-5-修改注册表" aria-hidden="true">#</a> 2.5 修改注册表</h3><div class="language-text line-numbers-mode" data-ext="text"><pre class="language-text"><code>计算机\\HKEY_LOCAL_MACHINE\\SYSTEM\\CurrentControlSet\\Services\\mysql5728
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><figure><img src="`+_+`" alt="image-20230505194327729" tabindex="0" loading="lazy"><figcaption>image-20230505194327729</figcaption></figure><p>将ImagePath修改为</p><div class="language-text line-numbers-mode" data-ext="text"><pre class="language-text"><code>&quot;C:/DISH/APP/JAVA/MySQL/mysql-5.7.28-winx64/bin/mysqld.exe&quot; --defaults-file=&quot;C:/DISH/APP/JAVA/MySQL/mysql-5.7.28-winx64/my.ini&quot; mysql5728
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><h3 id="_2-6-再次启动mysql5728就可以启动成功了" tabindex="-1"><a class="header-anchor" href="#_2-6-再次启动mysql5728就可以启动成功了" aria-hidden="true">#</a> 2.6 再次启动mysql5728就可以启动成功了</h3><figure><img src="`+f+'" alt="image-20230505195308164" tabindex="0" loading="lazy"><figcaption>image-20230505195308164</figcaption></figure><h2 id="_3-参考文件" tabindex="-1"><a class="header-anchor" href="#_3-参考文件" aria-hidden="true">#</a> 3 参考文件</h2>',10),Q={href:"https://www.zhangshengrong.com/p/l51gwbO710/",target:"_blank",rel:"noopener noreferrer"},w={href:"https://blog.csdn.net/qq_39293334/article/details/124762059",target:"_blank",rel:"noopener noreferrer"};function I(P,z){const s=l("ExternalLinkIcon");return c(),d("div",null,[x,a("p",null,[a("a",S,[n("MySQL Windows64 版ZIP下载网站"),e(s)])]),L,a("p",null,[a("a",M,[n("参考博客"),e(s)])]),A,a("p",null,[a("a",Q,[n("mysql 8.0.22压缩包完整安装与配置教程图解(亲测安装有效)"),e(s)])]),a("p",null,[a("a",w,[n("启动MySQL的时候，报错：More help is available by typing NET HELPMSG 3534."),e(s)])])])}const C=t(q,[["render",I],["__file","MySQL安装教程.html.vue"]]);export{C as default};
